INSERT INTO `preguntas` VALUES ('memoria', 'Nota problemas relacionados con el rendimiento de su P.C. despues de agregar mas memoria?');
INSERT INTO `preguntas` VALUES ('procesador', 'Parece que el procesador esta muerto; imposible de arrancar o no funciona correctamente?');
INSERT INTO `preguntas` VALUES ('procesador', 'Se esta reiniciando el sistema sin previo aviso o se esta agotando?');
