CREATE TABLE `tipos` (
  `tipoID` varchar(10) NOT NULL default '',
  `tipo` varchar(20) NOT NULL default ''
) ENGINE=InnoDB;


CREATE TABLE `preguntas` (
  `preguntaTIPO` varchar(20) NOT NULL default '',
  `preguntaDESCRIPCION` varchar(255) NOT NULL default ''
) ENGINE=InnoDB;





CREATE TABLE `problemas` (
  `problemaID` int(4) NOT NULL auto_increment,
  `preguntaTIPO` varchar(10) NOT NULL default '',
  `problemaDesc` varchar(20) NOT NULL default '',
  PRIMARY KEY  (`problemaID`)
)ENGINE=MyISAM AUTO_INCREMENT=94 ;




CREATE TABLE `recomendacion` (
  `problemaID` int(16) NOT NULL,
  `DescripcionRecomendacion` varchar(350)  NULL default ''
) ENGINE=InnoDB;


